$("#add").click(function(){
    window.location= appName + "/ap/12-example/12-customertickets"
})

function downloadFile(fileId){
    window.location= appName + "/Download/" + fileId + "/";
}

function downloadFileWidget(fileId){
    if(fileId == null || fileId == ""){
        return "&nbsp;";
    }
    return "<span onclick=\"downloadFile(" + fileId + ")\" class=\"spanOption fas fa-cloud-download-alt\" title=\"Download\"></span>";
}

function deleteRow(obj){
    var table = $('#customertickets').DataTable();
    var data = table.row($(obj).parents('tr')).data();
    alertify.confirm("Are you sure?",
        function(){
          doDelete(data.id);
        },
        function(){
            alertify.error("user canceled");
        }
    );
}

function doDelete(id){
    doBlockUiModal();
    $.ajax({
        type: 'POST',
        url: appName + '/ws/json/12-customertickets/delete',
        data: {
            id: id,
        },
        success: function(responseData){
            $.unblockUI();
            let jsonObject = eval(responseData);
            if(jsonObject.error) {
                errorMsg(jsonObject.error);
            } else {
                window.location =  appName + '/rp/12-example/12-customerticketsreport';
            }
        }
    })
}

